mapper.select:
SELECT NVL(COUNT(STS_SID), 0) AS STSCOUNT
FROM STS_CONDITION_VIEW
WHERE MEM_SID = #{memSid}

SELECT ISPTNAME
FROM SITTING_PLACE_TAGS_VIEW
WHERE SPSID = #{spSid}

SELECT SRWSID, SRWTITLE, SRWCONTENT, SRWRATE, SRWDATE, MEMSID, PMEMSID
FROM SITTING_REVIEW_VIEW
WHERE MEMSID = #{memSid}

SELECT JMNICKNAME, SPTITLE, SPADDR1, SPADDR2, EXTRAADDR, MEMSID, SPHSID, SPHSTART, SPHEND, SRSTART, SREND, SRWSID, STLESID, SPMAXPET, SPSID, GRADE, PRICE FROM SITTING_PLACE_LIST_VIEW WHERE 1=1
AND EXTRAADDR = #{extraAddr}
AND #{datePicker} NOT BETWEEN TO_CHAR(SRSTART, 'YYYY-MM-DD') AND TO_CHAR(SREND, 'YYYY-MM-DD')
AND SPMAXPET >= #{spMaxPet}
