mapper.insert:
INSERT INTO JOIN_MEMBER(JM_SID, MEM_SID, JM_ID, JM_PW, JM_NAME, JM_SSN, JM_GENDER, JM_NICKNAME, JM_TEL, JM_ADDR1, JM_ADDR2, JM_ZIPCODE, JM_MAGE)
VALUES(SEQ_JM.NEXTVAL, #{memSid}, #{jmId},CRYPTPACK.ENCRYPT(#{jmPw}, #{jmPw}), #{jmName}, #{jmSsn}, #{jmGender}
, #{jmNickName}, #{jmTel}, #{jmAddr1}, #{jmAddr2}, #{jmZipCode}, #{jmImage})

mapper.select:
SELECT NVL(MAX(JM_ID), '0') AS JM_ID
FROM JOIN_MEMBER
WHERE JM_NAME = #{jmName} AND JM_SSN = #{jmSsn}

mapper.update:
UPDATE JOIN_MEMBER
SET JM_PW = CRYPTPACK.ENCRYPT(#{jmPw}, #{jmPw})
WHERE MEM_SID = #{memSid}
